// from https://gist.github.com/dceejay/cea8afa28b7a93ebdc0f 
// if queue doesn't exist, create it
if (msg.hasOwnProperty("restartQueue")) {
    context.queue = []

} else {
    context.queue = context.queue || []
    context.busy = context.busy || false

    // if the msg is a trigger one release next message
    if (msg.hasOwnProperty("releaseNext")) {
        if (context.queue.length > 0) {
            var m = context.queue.shift()
            node.status({ fill: 'green', shape: 'dot', text: context.queue.length })
            return m 
        }
        else {
            context.busy = false;
        }
    }
    else {
        if (context.busy) {
            // if busy add to queue
            context.queue.push(msg);
        }
        else {
        // otherwise we are empty so just pass through and set busy flag
            context.busy = true;
            node.status({ fill: 'green', shape: 'dot', text: context.queue.length })
            return msg;
        }
    }
}
node.status({ fill: 'green', shape: 'dot', text: context.queue.length })

return null;
